import { useEffect, useState } from 'react';
import './App.css';
import Formulario from './Formulario';
import Tabela from './Tabela';

function App() {

  // UseState
  const [btnCadastrar, setBtnCadastrar] = useState(true);
  const [produtos, setProdutos] = useState([]);
  const [objProduto, setObjProjeto] = useState({
    "codigo":0,
    "nome":"",
    "marca":""
  });

  // UseEffect
  useEffect(()=>{
    fetch("http://localhost:8080/listar")
    .then(retorno => retorno.json())
    .then(retorno_convertido => setProdutos(retorno_convertido));
  }, []);

  const evento = (e) => {
    let nome = e.target.name;
    let valor = e.target.value;

    setObjProjeto({...objProduto, [nome]:valor})
  }

  const eventoCadastrar = () => {
    fetch("http://localhost:8080/cadastrar", {method:'POST', body:JSON.stringify(objProduto), headers: {"Content-type": "application/json;", "Accept":"application/json"}})
    .then(retorno => console.log(retorno.status))
    //.then(retorno => console.log(retorno));
  }

  // Retorno
  return (
    <div>
      <p>{JSON.stringify(objProduto)}</p>
      <Formulario botao={btnCadastrar} evento={evento} eventoCadastrar={eventoCadastrar} />
      <Tabela vetor={produtos} /> 
    </div>
  );
}

export default App;
